<?phpfunction cetak_gambar($total){    if($total%2 == 0){        echo('Hanya input bilangan ganjil!');    }else{         for($i=1; $i<=$total; $i++){        for($j=1; $j<=$total; $j++){            if($j == $i || $j-1 == $total-$i) {                echo '* ';            }else{                echo '= ';            }        }                echo "\r\n";    }       }}cetak_gambar(11);